import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AfficherAbsences() {
  const [absences, setAbsences] = useState([]);

  useEffect(() => {
    // Récupérer les absences depuis le serveur
    axios.get("http://localhost:5000/absences")
      .then(res => {
        setAbsences(res.data.data);
      });
  }, []);

  return (
    <div>
      <h1>Liste des Absences</h1>
      <table>
        <thead>
          <tr>
            <th>Nom</th>
            <th>Prénom</th>
            <th>Date</th>
            <th>Motif</th>
          </tr>
        </thead>
        <tbody>
          {absences.map((absence, index) => (
            <tr key={index}>
              <td>{absence.nom}</td>
              <td>{absence.prenom}</td>
              <td>{absence.date}</td>
              <td>{absence.motif}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AfficherAbsences;


