// AjoutAbsence.js
import React, { useState } from 'react';
import axios from 'axios';

function AjoutAbsence() {
  const [personneId, setPersonneId] = useState('');
  const [date, setDate] = useState('');
  const [motif, setMotif] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/ajouter-absence", {
        personneId,
        date,
        motif,
      });
      alert("Absence ajoutée avec succès !");
    } catch (error) {
      console.error(error);
      alert("Erreur lors de l'ajout de l'absence");
    }
  };

  return (
    <div>
      <h2>Ajouter une Absence</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Personne :
          <input
            type="text"
            value={personneId}
            onChange={(e) => setPersonneId(e.target.value)}
          />
        </label>
        <label>
          Date :
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </label>
        <label>
          Motif :
          <input
            type="text"
            value={motif}
            onChange={(e) => setMotif(e.target.value)}
          />
        </label>
        <button type="submit">Ajouter Absence</button>
      </form>
    </div>
  );
}

export default AjoutAbsence;
