import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AjouterAbsence() {
  const [personnes, setPersonnes] = useState([]);
  const [selectedPersonne, setSelectedPersonne] = useState('');
  const [date, setDate] = useState('');
  const [motif, setMotif] = useState('');

  useEffect(() => {
    // Récupérer la liste des personnes depuis le serveur
    axios.get("http://localhost:5000/persone")
      .then(res => {
        setPersonnes(res.data.data);
      });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Envoyer les données d'absence au serveur
    axios.post("http://localhost:5000/ajouter-absence", {
      personneId: selectedPersonne,
      date,
      motif
    })
    .then(res => {
      // Traiter la réponse du serveur, par exemple, afficher un message de succès
      console.log(res.data);
      // Réinitialiser le formulaire
      setSelectedPersonne('');
      setDate('');
      setMotif('');
    })
    .catch(error => {
      // Gérer les erreurs, par exemple, afficher un message d'erreur
      console.error(error);
    });
  };

  return (
    <div>
      <h1>Ajouter une Absence</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Sélectionnez la personne :</label>
          <select
            value={selectedPersonne}
            onChange={(e) => setSelectedPersonne(e.target.value)}
          >
            <option value="">Sélectionnez une personne</option>
            {personnes.map((personne) => (
              <option key={personne.id} value={personne.id}>
                {personne.nom} {personne.prenom}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label>Date :</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </div>
        <div>
          <label>Motif :</label>
          <input
            type="text"
            value={motif}
            onChange={(e) => setMotif(e.target.value)}
          />
        </div>
        <button type="submit">Ajouter Absence</button>
      </form>
    </div>
  );
}

export default AjouterAbsence;
