import React, { useState, useEffect } from 'react';
import axios from 'axios';

function GestionAbsences() {
  const [personnes, setPersonnes] = useState([]);
  const [personnesSelectionnees, setPersonnesSelectionnees] = useState([]);
  const [nouvelleAbsence, setNouvelleAbsence] = useState({
    date: '',
    motif: ''
  });

  useEffect(() => {
    // Récupérer la liste des personnes depuis le serveur
    axios.post("http://localhost:5000/persone")
      .then(res => {
        setPersonnes(res.data.data);
      });
  }, []);

  const handleCheckboxChange = (e, personne) => {
    const isChecked = e.target.checked;
    if (isChecked) {
      setPersonnesSelectionnees([...personnesSelectionnees, personne]);
    } else {
      setPersonnesSelectionnees(personnesSelectionnees.filter(p => p !== personne));
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNouvelleAbsence({ ...nouvelleAbsence, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Envoyer les données d'absence au serveur pour chaque personne sélectionnée
    personnes.forEach(personne => {
      const isSelected = personnesSelectionnees.includes(personne);
      const motif = isSelected ? "Oui" : "Non";

      axios.post("http://localhost:5000/ajouter-absence", {
        personne: personne.id,
        motif,
        date: nouvelleAbsence.date,
      })
      .then(res => {
        // Mettre à jour l'état local avec les nouvelles données (si nécessaire)
        // Réinitialiser les cases à cocher (si nécessaire)
        // Réinitialiser le formulaire d'ajout d'absence
        setNouvelleAbsence({ date: '', motif: '' });
      });
    });
  };

  return (
    <div>
      <h1>Gestion des absences</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Sélectionnez les personnes :</label>
          {personnes.map((personne, index) => (
            <div key={index}>
              <input
                type="checkbox"
                name={`personne-${personne.id}`}
                checked={personnesSelectionnees.includes(personne)}
                onChange={(e) => handleCheckboxChange(e, personne)}
              />
              {personne.nom} - {personnesSelectionnees.includes(personne) ? "Oui" : "Non"}
            </div>
          ))}
        </div>
        <div>
          <label>Date :</label>
          <input
            type="date"
            name="date"
            value={nouvelleAbsence.date}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Motif :</label>
          <input
            type="text"
            name="motif"
            value={nouvelleAbsence.motif}
            onChange={handleInputChange}
          />
        </div>
        <button type="submit">Ajouter absence</button>
      </form>
      {/* Affichez les absences pour chaque personne ici */}
    </div>
  );
}

export default GestionAbsences;
