import React, { useEffect } from 'react'
import axios from "axios"
import { useState } from 'react'
import { Link } from 'react-router-dom'
// import './patient.css'
// import Swal from 'sweetalert2'
import SideNavBar from '../../SideNavBar/SideNavBar'
function Patients() {
    // const [cin, setcin] = useState("");
    const [date, setdate] = useState(new Date().toISOString().split('T')[0]);
    const [patient, setPatient] = useState([])
    const [patientItem, setPatientItem] = useState([])
    const [absances, setabsances] = useState([])


    useEffect(() => {
        axios.post("http://localhost:5000/persone")
        .then(res => {
            const use = res.data.data;
            setPatient(use)
            setPatientItem(use)
        });
    },[]);  

    const Rechercher = (e) => {
        if (e.target.value === "") {
            setPatient(patientItem)
            return;
        }
        else {
            const result = patient.filter(item => item.nom.toLowerCase().search(e.target.value.toLowerCase()) !== -1)
            setPatient(result)
        }
    }
    const Comp=(id)=>{
        setabsances([...absances, {id:id, date: date, notif: "on" }]);
    }

    const addabcences=(e)=>{
        e.preventDefault();
        axios.post("http://localhost:5000/ajouter-absence", absances)
        .then(Response=>{
           console.log(Response.data)
        });
    }

    const mapping = patient && patient.map(item => (
        <tr style={{backgroundColor: '#EAEDED '}} align='center' key={item._id}>
            <td className='p-0' colSpan={1}><img src={`/images/files-persones/${item.image}`} alt="" width={70} height={70}/></td>
            <td colSpan={1}><input type="text" className="form-control" id="cin" name='cin' value={item.cin} disabled /></td>
            <td colSpan={1}><input type="text" className="form-control" id="nom" name='nom' value={item.nom} disabled /></td>
            <td colSpan={1}><input type="text" className="form-control" id="prenom" name='prenom' value={item.prenom} disabled /></td>
            <td colSpan={1}>
                <div class="form-check form-switch">
                    <input className='form-check-input' type="checkbox" id='notif' name={item._id} onChange={()=>Comp(item._id)} />
                </div>
            </td>
        </tr>
    ))
    return (

        
    <div className="bodycontant">
        <div className='sidebar'>
            <SideNavBar/>
        </div> 
        <div className='content'></div>
        <div className='container mx-2 mt-1' >
            <div className='container'>
            <h1 className='mt-1'>
                Personne
            </h1>
            <div className='searchBar my-2'>
                <Link to={"/AjouterPatient"} className='btn btn-success mx-2'>Ajouter</Link>
                <input className="form-control mr-sm-2 mx-1" type="search" onChange={Rechercher} placeholder="Rechercher Persone avec le Nom" aria-label="Search" />
            </div>
            <table className="table table-fixed">
                <thead style={{backgroundColor: '#D7DBDD'}}>
                    <tr align='center'>
                        <td colSpan={1}>image</td>
                        <td colSpan={1}>Cin</td> 
                        <td colSpan={1}>Nom</td>
                        <td colSpan={1}>Pr√©nom</td>
                        <td colSpan={1}>notif</td>
                        <td colSpan={2}>Action</td>
                    </tr>
                </thead>
                <tbody >
                    {mapping}
                    <tr>
                        <td className='chose'colSpan={2}>
                            <button className='btn btn-s btn-sm '  onClick={()=>addabcences()}>Enregistrer</button>
                        </td>
                    </tr>
                </tbody>
            </table>
        </div>
        </div>
    </div>
        
    )
}

export default Patients
